	$templateCode .= loadView('acceso_remoto-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: acceso_remoto_dv
	if(function_exists('acceso_remoto_dv')) {
		$args=[];
		acceso_remoto_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}