function articulos_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('articulos', $selected_id, 'edit')) return false;

	$data = [
		'tipo_dispo' => Request::lookup('tipo_dispo', ''),
		'marca' => Request::lookup('marca', ''),
		'modelo' => Request::lookup('modelo', ''),
		'presentacion' => Request::lookup('presentacion', ''),
		'serial' => Request::val('serial', ''),
		'referencia' => Request::val('referencia', ''),
		'familia' => Request::lookup('familia', ''),
		'codigo_proveedor' => Request::lookup('familia'),
		'caracteristicas' => br2nl(Request::val('caracteristicas', '')),
		'nota' => Request::val('nota', ''),
		'ubicacion' => Request::lookup('ubicacion', ''),
		'ubicacion_abreviado' => Request::lookup('ubicacion'),
		'imagen' => Request::lookup('modelo'),
		'estado' => Request::lookup('estado', ''),
	];

	if($data['presentacion'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Presentacion': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	// get existing values
	$old_data = getRecord('articulos', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: articulos_before_update
	if(function_exists('articulos_before_update')) {
		$args = ['old_data' => $old_data];
		if(!articulos_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'articulos', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="articulos_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('articulos', $data['selectedID'], calculated_fields()['articulos']);

	// hook: articulos_after_update
	if(function_exists('articulos_after_update')) {
		$res = sql("SELECT * FROM `articulos` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!articulos_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='articulos' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

