	$templateCode = str_replace('<%%UPLOADFILE(firma)%%>', ($noUploads ? '' : "<div>{$Translation['upload image']}</div>" . '<i class="glyphicon glyphicon-remove text-danger clear-upload hidden"></i> <input type="file" name="firma" id="firma" data-filetypes="jpg|jpeg|gif|png" data-maxsize="1024000" accept=".jpg,.jpeg,.gif,.png">'), $templateCode);

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id_contacto)%%>', safe_html($urow['id_contacto']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id_contacto)%%>', html_attr($row['id_contacto']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id_contacto)%%>', urlencode($urow['id_contacto']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(registro_invima)%%>', safe_html($urow['registro_invima']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(registro_invima)%%>', html_attr($row['registro_invima']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(registro_invima)%%>', urlencode($urow['registro_invima']), $templateCode);
		if($dvprint || (!$AllowUpdate && !$AllowInsert)) {
			$templateCode = str_replace('<%%VALUE(nota)%%>', safe_html($urow['nota']), $templateCode);
		} else {
			$templateCode = str_replace('<%%VALUE(nota)%%>', safe_html($urow['nota'], true), $templateCode);
		}
		$templateCode = str_replace('<%%URLVALUE(nota)%%>', urlencode($urow['nota']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(firma)%%>', safe_html($urow['firma']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(firma)%%>', html_attr($row['firma']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(firma)%%>', urlencode($urow['firma']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(id_contacto)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id_contacto)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(registro_invima)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(registro_invima)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(nota)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(nota)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(firma)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(firma)%%>', urlencode(''), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';

	$templateCode .= "\tid_contacto_update_autofills$rnd1 = function() {\n";
	$templateCode .= "\t\t\$j.ajax({\n";
	if($dvprint) {
		$templateCode .= "\t\t\turl: 'biomedicos_autofill.php?rnd1=$rnd1&mfk=id_contacto&id=' + encodeURIComponent('".addslashes($row['id_contacto'])."'),\n";
		$templateCode .= "\t\t\tcontentType: 'application/x-www-form-urlencoded; charset=" . datalist_db_encoding . "',\n";
		$templateCode .= "\t\t\ttype: 'GET'\n";
	} else {
		$templateCode .= "\t\t\turl: 'biomedicos_autofill.php?rnd1=$rnd1&mfk=id_contacto&id=' + encodeURIComponent(AppGini.current_id_contacto{$rnd1}.value),\n";
		$templateCode .= "\t\t\tcontentType: 'application/x-www-form-urlencoded; charset=" . datalist_db_encoding . "',\n";
		$templateCode .= "\t\t\ttype: 'GET',\n";
		$templateCode .= "\t\t\tbeforeSend: function() { \$j('#id_contacto$rnd1').prop('disabled', true); \$j('#id_contactoLoading').html('<img src=loading.gif align=top>'); },\n";
		$templateCode .= "\t\t\tcomplete: function() { " . (($arrPerm[1] || (($arrPerm[3] == 1 && $ownerMemberID == getLoggedMemberID()) || ($arrPerm[3] == 2 && $ownerGroupID == getLoggedGroupID()) || $arrPerm[3] == 3)) ? "\$j('#id_contacto$rnd1').prop('disabled', false); " : "\$j('#id_contacto$rnd1').prop('disabled', true); ")."\$j('#id_contactoLoading').html(''); \$j(window).resize(); }\n";
	}
	$templateCode .= "\t\t});\n";
	$templateCode .= "\t};\n";
	if(!$dvprint) $templateCode .= "\tif(\$j('#id_contacto_caption').length) \$j('#id_contacto_caption').click(function() { id_contacto_update_autofills$rnd1(); });\n";


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('biomedicos');
	if($selected_id) {
		$jdata = get_joined_record('biomedicos', $selected_id);
		if($jdata === false) $jdata = get_defaults('biomedicos');
		$rdata = $row;
	}
	$templateCode .= loadView('biomedicos-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: biomedicos_dv
	if(function_exists('biomedicos_dv')) {
		$args=[];
		biomedicos_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}