	if(function_exists('costos_mtto_before_delete')) {
		$args = [];
		if(!costos_mtto_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: '' 
			);
	}

	sql("DELETE FROM `costos_mtto` WHERE `id`='{$selected_id}'", $eo);

	// hook: costos_mtto_after_delete
	if(function_exists('costos_mtto_after_delete')) {
		$args = [];
