function cronograma_mtto_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('cronograma_mtto');
	if(!$arrPerm['insert']) return false;

	$data = [
		'ano' => Request::lookup('id_programa'),
		'codigo' => Request::lookup('codigo', ''),
		'tipo_dispo' => Request::lookup('codigo'),
		'marca' => Request::lookup('marca', ''),
		'modelo' => Request::lookup('codigo'),
		'periodicidad' => Request::lookup('periodicidad', ''),
		'meses' => Request::multipleChoice('meses', ''),
		'biomedico' => Request::val('biomedico', ''),
		'actividades' => Request::val('actividades', ''),
		'creado' => parseCode('<%%creationDateTime%%>', true, true),
		'creado_por' => parseCode('<%%creatorUsername%%>', true),
	];

	if($data['meses'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Meses': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}

	// hook: cronograma_mtto_before_insert
	if(function_exists('cronograma_mtto_before_insert')) {
		$args = [];
		if(!cronograma_mtto_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('cronograma_mtto', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	// automatic id_programa if passed as filterer
	if($_REQUEST['filterer_id_programa']) {
		sql("UPDATE `cronograma_mtto` SET `id_programa`='" . makeSafe($_REQUEST['filterer_id_programa']) . "' WHERE `id`='" . makeSafe($recID, false) . "'", $eo);
	}

	update_calc_fields('cronograma_mtto', $recID, calculated_fields()['cronograma_mtto']);

	// hook: cronograma_mtto_after_insert
	if(function_exists('cronograma_mtto_after_insert')) {
		$res = sql("SELECT * FROM `cronograma_mtto` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=[];
		if(!cronograma_mtto_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('cronograma_mtto', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) cronograma_mtto_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

