function cronograma_mtto_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('cronograma_mtto', $selected_id, 'edit')) return false;

	$data = [
		'ano' => Request::lookup('id_programa'),
		'codigo' => Request::lookup('codigo', ''),
		'tipo_dispo' => Request::lookup('codigo'),
		'marca' => Request::lookup('marca', ''),
		'modelo' => Request::lookup('codigo'),
		'periodicidad' => Request::lookup('periodicidad', ''),
		'meses' => Request::multipleChoice('meses', ''),
		'biomedico' => Request::val('biomedico', ''),
		'actividades' => Request::val('actividades', ''),
	];

	if($data['meses'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Meses': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	// get existing values
	$old_data = getRecord('cronograma_mtto', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: cronograma_mtto_before_update
	if(function_exists('cronograma_mtto_before_update')) {
		$args = ['old_data' => $old_data];
		if(!cronograma_mtto_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'cronograma_mtto', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="cronograma_mtto_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('cronograma_mtto', $data['selectedID'], calculated_fields()['cronograma_mtto']);

	// hook: cronograma_mtto_after_update
	if(function_exists('cronograma_mtto_after_update')) {
		$res = sql("SELECT * FROM `cronograma_mtto` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!cronograma_mtto_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='cronograma_mtto' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

