function dispo_fuera_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('dispo_fuera');
	if(!$arrPerm['insert']) return false;

	$data = [
		'img' => Request::lookup('codigo'),
		'codigo' => Request::lookup('codigo', ''),
		'serial' => Request::lookup('codigo'),
		'ubicacion_final' => Request::lookup('codigo'),
		'tipo_fuera_servicio' => Request::lookup('tipo_fuera_servicio', ''),
		'fecha_reporte' => Request::dateComponents('fecha_reporte', ''),
		'descripcion' => br2nl(Request::val('descripcion', '')),
		'creado' => parseCode('<%%creationDateTime%%>', true, true),
		'creado_por' => parseCode('<%%creatorUsername%%>', true),
	];

	if($data['tipo_fuera_servicio'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Tipo fuera servicio': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['fecha_reporte'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Fecha de reporte': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}

	// hook: dispo_fuera_before_insert
	if(function_exists('dispo_fuera_before_insert')) {
		$args = [];
		if(!dispo_fuera_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('dispo_fuera', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	update_calc_fields('dispo_fuera', $recID, calculated_fields()['dispo_fuera']);

	// hook: dispo_fuera_after_insert
	if(function_exists('dispo_fuera_after_insert')) {
		$res = sql("SELECT * FROM `dispo_fuera` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=[];
		if(!dispo_fuera_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('dispo_fuera', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) dispo_fuera_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

