function dispo_fuera_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('dispo_fuera', $selected_id, 'edit')) return false;

	$data = [
		'img' => Request::lookup('codigo'),
		'codigo' => Request::lookup('codigo', ''),
		'serial' => Request::lookup('codigo'),
		'ubicacion_final' => Request::lookup('codigo'),
		'tipo_fuera_servicio' => Request::lookup('tipo_fuera_servicio', ''),
		'fecha_reporte' => Request::dateComponents('fecha_reporte', ''),
		'descripcion' => br2nl(Request::val('descripcion', '')),
		'editado' => parseCode('<%%editingDateTime%%>', false, true),
		'edtado_por' => parseCode('<%%editorUsername%%>', false),
	];

	if($data['tipo_fuera_servicio'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Tipo fuera servicio': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['fecha_reporte'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Fecha de reporte': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	// get existing values
	$old_data = getRecord('dispo_fuera', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: dispo_fuera_before_update
	if(function_exists('dispo_fuera_before_update')) {
		$args = ['old_data' => $old_data];
		if(!dispo_fuera_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'dispo_fuera', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="dispo_fuera_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('dispo_fuera', $data['selectedID'], calculated_fields()['dispo_fuera']);

	// hook: dispo_fuera_after_update
	if(function_exists('dispo_fuera_after_update')) {
		$res = sql("SELECT * FROM `dispo_fuera` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!dispo_fuera_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='dispo_fuera' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

