function dispositivos_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('dispositivos', $selected_id, 'edit')) return false;

	$data = [
		'foto' => Request::lookup('modelo'),
		'serial' => Request::val('serial', ''),
		'tipo_dispositivo' => Request::lookup('tipo_dispositivo', ''),
		'marca' => Request::lookup('marca', ''),
		'modelo' => Request::lookup('modelo', ''),
		'ubicacion_abre' => Request::lookup('ubicacion'),
		'fecha_ingreso' => Request::dateComponents('fecha_ingreso', ''),
		'clasificacion_riesgo' => Request::lookup('clasificacion_riesgo', ''),
		'estado' => Request::lookup('estado', ''),
		'editado' => parseCode('<%%editingDateTime%%>', false, true),
		'editado_por' => parseCode('<%%editorUsername%%>', false),
		'id_compra_ingreso' => Request::lookup('id_compra_ingreso', ''),
	];

	if($data['serial'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Serial': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['tipo_dispositivo'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Nombre del equipo': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['marca'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Marca': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['modelo'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Modelo': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['fecha_ingreso'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Fecha de ingreso': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['clasificacion_riesgo'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Riesgo': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['estado'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Estado': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	// get existing values
	$old_data = getRecord('dispositivos', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: dispositivos_before_update
	if(function_exists('dispositivos_before_update')) {
		$args = ['old_data' => $old_data];
		if(!dispositivos_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'dispositivos', 
		backtick_keys_once($set), 
		['`id_dispo`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="dispositivos_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('dispositivos', $data['selectedID'], calculated_fields()['dispositivos']);

	// hook: dispositivos_after_update
	if(function_exists('dispositivos_after_update')) {
		$res = sql("SELECT * FROM `dispositivos` WHERE `id_dispo`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id_dispo'];
		$args = ['old_data' => $old_data];
		if(!dispositivos_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='dispositivos' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

