	$data = [
		'tipo_dispo' => Request::lookup('tipo_dispo', ''),
		'marca' => Request::lookup('marca', ''),
		'modelo' => Request::lookup('modelo', ''),
		'tipo_documento' => Request::lookup('tipo_documento', ''),
		'enlace' => Request::val('enlace', ''),
		'descripcion' => br2nl(Request::val('descripcion', '')),
		'creado' => parseCode('<%%creationDateTime%%>', true, true),
		'creado_por' => parseCode('<%%creatorUsername%%>', true),
	];


	// hook: docu_modelo_before_insert
	if(function_exists('docu_modelo_before_insert')) {
		$args = [];
		if(!docu_modelo_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('docu_modelo', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	update_calc_fields('docu_modelo', $recID, calculated_fields()['docu_modelo']);

	// hook: docu_modelo_after_insert
	if(function_exists('docu_modelo_after_insert')) {
		$res = sql("SELECT * FROM `docu_modelo` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=[];
		if(!docu_modelo_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('docu_modelo', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) docu_modelo_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

