function docu_modelo_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('docu_modelo', $selected_id, 'edit')) return false;

	$data = [
		'tipo_dispo' => Request::lookup('tipo_dispo', ''),
		'marca' => Request::lookup('marca', ''),
		'modelo' => Request::lookup('modelo', ''),
		'tipo_documento' => Request::lookup('tipo_documento', ''),
		'enlace' => Request::val('enlace', ''),
		'descripcion' => br2nl(Request::val('descripcion', '')),
		'editado' => parseCode('<%%editingDateTime%%>', false, true),
		'editado_por' => parseCode('<%%editorUsername%%>', false),
	];

	// get existing values
	$old_data = getRecord('docu_modelo', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: docu_modelo_before_update
	if(function_exists('docu_modelo_before_update')) {
		$args = ['old_data' => $old_data];
		if(!docu_modelo_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'docu_modelo', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="docu_modelo_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('docu_modelo', $data['selectedID'], calculated_fields()['docu_modelo']);

	// hook: docu_modelo_after_update
	if(function_exists('docu_modelo_after_update')) {
		$res = sql("SELECT * FROM `docu_modelo` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!docu_modelo_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='docu_modelo' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

