			$templateCode.="\n\tif(document.getElementById('enlaceEdit')) { document.getElementById('enlaceEdit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('enlaceEditLink')) { document.getElementById('enlaceEditLink').style.display='none'; }";
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('docu_modelo');
	if($selected_id) {
		$jdata = get_joined_record('docu_modelo', $selected_id);
		if($jdata === false) $jdata = get_defaults('docu_modelo');
		$rdata = $row;
	}
	$templateCode .= loadView('docu_modelo-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: docu_modelo_dv
	if(function_exists('docu_modelo_dv')) {
		$args=[];
		docu_modelo_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}