		if(!$_REQUEST['Embedded']) $templateCode = str_replace('<%%DVPRINT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="dvprint" name="dvprint_x" value="1" onclick="$j(\'form\').eq(0).prop(\'novalidate\', true); document.myform.reset(); return true;" title="' . html_attr($Translation['Print Preview']) . '"><i class="glyphicon glyphicon-print"></i> ' . $Translation['Print Preview'] . '</button>', $templateCode);
		if($AllowUpdate) {
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '<button type="submit" class="btn btn-success btn-lg" id="update" name="update_x" value="1" onclick="return formatos_validateData();" title="' . html_attr($Translation['Save Changes']) . '"><i class="glyphicon glyphicon-ok"></i> ' . $Translation['Save Changes'] . '</button>', $templateCode);
		} else {
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		}
		if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3) { // allow delete?
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '<button type="submit" class="btn btn-danger" id="delete" name="delete_x" value="1" onclick="return confirm(\'' . $Translation['are you sure?'] . '\');" title="' . html_attr($Translation['Delete']) . '"><i class="glyphicon glyphicon-trash"></i> ' . $Translation['Delete'] . '</button>', $templateCode);
		} else {
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		}
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', ($ShowCancel ? '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>' : ''), $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(($selected_id && !$AllowUpdate && !$AllowInsert) || (!$selected_id && !$AllowInsert)) {
		$jsReadOnly = '';
		$jsReadOnly .= "\tjQuery('#tipo_formato').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#tipo_formato_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#tipo_dispositivo').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#tipo_dispositivo_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#nombre_formato').replaceWith('<div class=\"form-control-static\" id=\"nombre_formato\">' + (jQuery('#nombre_formato').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#formato, #formato-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('#descripcion').replaceWith('<div class=\"form-control-static\" id=\"descripcion\">' + (jQuery('#descripcion').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	} elseif($AllowInsert) {
		$jsEditable = "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(tipo_formato)%%>', $combo_tipo_formato->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(tipo_formato)%%>', $combo_tipo_formato->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(tipo_formato)%%>', urlencode($combo_tipo_formato->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(tipo_dispositivo)%%>', $combo_tipo_dispositivo->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(tipo_dispositivo)%%>', $combo_tipo_dispositivo->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(tipo_dispositivo)%%>', urlencode($combo_tipo_dispositivo->MatchText), $templateCode);

	/* lookup fields array: 'lookup field name' => array('parent table name', 'lookup field caption') */
	$lookup_fields = array('tipo_formato' => array('tipo_formatos', 'Tipo formato'), 'tipo_dispositivo' => array('tipo_dispositivo', 'Tipo de dispositivo'), );
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent hspacer-md" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] /* && !$_REQUEST['Embedded']*/) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(tipo_formato)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(tipo_dispositivo)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(nombre_formato)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(formato)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(descripcion)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(creado)%%>', '', $templateCode);

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(tipo_formato)%%>', safe_html($urow['tipo_formato']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(tipo_formato)%%>', html_attr($row['tipo_formato']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(tipo_formato)%%>', urlencode($urow['tipo_formato']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(tipo_dispositivo)%%>', safe_html($urow['tipo_dispositivo']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(tipo_dispositivo)%%>', html_attr($row['tipo_dispositivo']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(tipo_dispositivo)%%>', urlencode($urow['tipo_dispositivo']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(nombre_formato)%%>', safe_html($urow['nombre_formato']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(nombre_formato)%%>', html_attr($row['nombre_formato']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(nombre_formato)%%>', urlencode($urow['nombre_formato']), $templateCode);
		if($AllowUpdate || $AllowInsert) {
			$templateCode = str_replace('<%%HTMLAREA(formato)%%>', '<textarea name="formato" id="formato" rows="5">' . html_attr($row['formato']) . '</textarea>', $templateCode);
		} else {
			$templateCode = str_replace('<%%HTMLAREA(formato)%%>', '<div id="formato" class="form-control-static">' . $row['formato'] . '</div>', $templateCode);
		}
		$templateCode = str_replace('<%%VALUE(formato)%%>', nl2br($row['formato']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(formato)%%>', urlencode($urow['formato']), $templateCode);
		if($dvprint || (!$AllowUpdate && !$AllowInsert)) {
			$templateCode = str_replace('<%%VALUE(descripcion)%%>', safe_html($urow['descripcion']), $templateCode);
		} else {
			$templateCode = str_replace('<%%VALUE(descripcion)%%>', safe_html($urow['descripcion'], true), $templateCode);
		}
		$templateCode = str_replace('<%%URLVALUE(descripcion)%%>', urlencode($urow['descripcion']), $templateCode);
		$templateCode = str_replace('<%%VALUE(creado)%%>', app_datetime($row['creado'], 'dt'), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(creado)%%>', urlencode(app_datetime($urow['creado'], 'dt')), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(tipo_formato)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(tipo_formato)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(tipo_dispositivo)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(tipo_dispositivo)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(nombre_formato)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(nombre_formato)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%HTMLAREA(formato)%%>', '<textarea name="formato" id="formato" rows="5"></textarea>', $templateCode);
		$templateCode = str_replace('<%%VALUE(descripcion)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(descripcion)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(creado)%%>', '<%%creationDateTime%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(creado)%%>', urlencode('<%%creationDateTime%%>'), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
			$templateCode.="\n\tif(document.getElementById('formatoEdit')) { document.getElementById('formatoEdit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('formatoEditLink')) { document.getElementById('formatoEditLink').style.display='none'; }";
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('formatos');
	if($selected_id) {
		$jdata = get_joined_record('formatos', $selected_id);
		if($jdata === false) $jdata = get_defaults('formatos');
		$rdata = $row;
	}
	$templateCode .= loadView('formatos-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: formatos_dv
	if(function_exists('formatos_dv')) {
		$args=[];
		formatos_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}