	$data = [
		'tipo_documento_entrega' => Request::val('tipo_documento_entrega', ''),
		'ciudad' => Request::lookup('ciudad', ''),
		'fecha' => Request::dateComponents('fecha', ''),
		'entidad' => Request::lookup('entidad', ''),
		'referencia' => Request::val('referencia', ''),
		'descripcion' => Request::val('descripcion', ''),
		'conslusion' => Request::val('conslusion', ''),
		'creado' => parseCode('<%%creationDateTime%%>', true, true),
		'creado_por' => parseCode('<%%creatorUsername%%>', true),
	];

	if($data['fecha'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Fecha': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}

	// hook: genera_documentos_before_insert
	if(function_exists('genera_documentos_before_insert')) {
		$args = [];
		if(!genera_documentos_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('genera_documentos', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	update_calc_fields('genera_documentos', $recID, calculated_fields()['genera_documentos']);

	// hook: genera_documentos_after_insert
	if(function_exists('genera_documentos_after_insert')) {
		$res = sql("SELECT * FROM `genera_documentos` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=[];
		if(!genera_documentos_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('genera_documentos', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) genera_documentos_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

