function genera_documentos_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('genera_documentos', $selected_id, 'edit')) return false;

	$data = [
		'tipo_documento_entrega' => Request::val('tipo_documento_entrega', ''),
		'ciudad' => Request::lookup('ciudad', ''),
		'fecha' => Request::dateComponents('fecha', ''),
		'entidad' => Request::lookup('entidad', ''),
		'referencia' => Request::val('referencia', ''),
		'descripcion' => Request::val('descripcion', ''),
		'conslusion' => Request::val('conslusion', ''),
	];

	if($data['fecha'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Fecha': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	// get existing values
	$old_data = getRecord('genera_documentos', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: genera_documentos_before_update
	if(function_exists('genera_documentos_before_update')) {
		$args = ['old_data' => $old_data];
		if(!genera_documentos_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'genera_documentos', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="genera_documentos_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('genera_documentos', $data['selectedID'], calculated_fields()['genera_documentos']);

	// hook: genera_documentos_after_update
	if(function_exists('genera_documentos_after_update')) {
		$res = sql("SELECT * FROM `genera_documentos` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!genera_documentos_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='genera_documentos' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

