		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', ($ShowCancel ? '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>' : ''), $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(($selected_id && !$AllowUpdate && !$AllowInsert) || (!$selected_id && !$AllowInsert)) {
		$jsReadOnly = '';
		$jsReadOnly .= "\tjQuery('#tipo_documento_entrega').replaceWith('<div class=\"form-control-static\" id=\"tipo_documento_entrega\">' + (jQuery('#tipo_documento_entrega').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#ciudad').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#ciudad_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#fecha').prop('readonly', true);\n";
		$jsReadOnly .= "\tjQuery('#fechaDay, #fechaMonth, #fechaYear').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#entidad').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#entidad_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#referencia').replaceWith('<div class=\"form-control-static\" id=\"referencia\">' + (jQuery('#referencia').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	} elseif($AllowInsert) {
		$jsEditable = "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(ciudad)%%>', $combo_ciudad->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(ciudad)%%>', $combo_ciudad->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(ciudad)%%>', urlencode($combo_ciudad->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(fecha)%%>', ($selected_id && !$arrPerm[3] ? '<div class="form-control-static">' . $combo_fecha->GetHTML(true) . '</div>' : $combo_fecha->GetHTML()), $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(fecha)%%>', $combo_fecha->GetHTML(true), $templateCode);
	$templateCode = str_replace('<%%COMBO(entidad)%%>', $combo_entidad->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(entidad)%%>', $combo_entidad->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(entidad)%%>', urlencode($combo_entidad->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(creado)%%>', ($selected_id && !$arrPerm[3] ? '<div class="form-control-static">' . $combo_creado->GetHTML(true) . '</div>' : $combo_creado->GetHTML()), $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(creado)%%>', $combo_creado->GetHTML(true), $templateCode);

	/* lookup fields array: 'lookup field name' => array('parent table name', 'lookup field caption') */
	$lookup_fields = array('ciudad' => array('ciudades', 'Ciudad'), 'entidad' => array('unidades', 'Entidad'), );
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent hspacer-md" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] /* && !$_REQUEST['Embedded']*/) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(tipo_documento_entrega)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(ciudad)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(fecha)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(entidad)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(referencia)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(descripcion)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(conslusion)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(creado)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(creado_por)%%>', '', $templateCode);

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(tipo_documento_entrega)%%>', safe_html($urow['tipo_documento_entrega']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(tipo_documento_entrega)%%>', html_attr($row['tipo_documento_entrega']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(tipo_documento_entrega)%%>', urlencode($urow['tipo_documento_entrega']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(ciudad)%%>', safe_html($urow['ciudad']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(ciudad)%%>', html_attr($row['ciudad']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ciudad)%%>', urlencode($urow['ciudad']), $templateCode);
		$templateCode = str_replace('<%%VALUE(fecha)%%>', @date('d/m/Y', @strtotime(html_attr($row['fecha']))), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(fecha)%%>', urlencode(@date('d/m/Y', @strtotime(html_attr($urow['fecha'])))), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(entidad)%%>', safe_html($urow['entidad']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(entidad)%%>', html_attr($row['entidad']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(entidad)%%>', urlencode($urow['entidad']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(referencia)%%>', safe_html($urow['referencia']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(referencia)%%>', html_attr($row['referencia']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(referencia)%%>', urlencode($urow['referencia']), $templateCode);
		if($AllowUpdate || $AllowInsert) {
			$templateCode = str_replace('<%%HTMLAREA(descripcion)%%>', '<textarea name="descripcion" id="descripcion" rows="5">' . html_attr($row['descripcion']) . '</textarea>', $templateCode);
		} else {
			$templateCode = str_replace('<%%HTMLAREA(descripcion)%%>', '<div id="descripcion" class="form-control-static">' . $row['descripcion'] . '</div>', $templateCode);
		}
		$templateCode = str_replace('<%%VALUE(descripcion)%%>', nl2br($row['descripcion']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(descripcion)%%>', urlencode($urow['descripcion']), $templateCode);
		if($AllowUpdate || $AllowInsert) {
			$templateCode = str_replace('<%%HTMLAREA(conslusion)%%>', '<textarea name="conslusion" id="conslusion" rows="5">' . html_attr($row['conslusion']) . '</textarea>', $templateCode);
		} else {
			$templateCode = str_replace('<%%HTMLAREA(conslusion)%%>', '<div id="conslusion" class="form-control-static">' . $row['conslusion'] . '</div>', $templateCode);
		}
		$templateCode = str_replace('<%%VALUE(conslusion)%%>', nl2br($row['conslusion']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(conslusion)%%>', urlencode($urow['conslusion']), $templateCode);
		$templateCode = str_replace('<%%VALUE(creado)%%>', @date('d/m/Y', @strtotime(html_attr($row['creado']))), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(creado)%%>', urlencode(@date('d/m/Y', @strtotime(html_attr($urow['creado'])))), $templateCode);
		$templateCode = str_replace('<%%VALUE(creado_por)%%>', safe_html($urow['creado_por']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(creado_por)%%>', urlencode($urow['creado_por']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(tipo_documento_entrega)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(tipo_documento_entrega)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(ciudad)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ciudad)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(fecha)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(fecha)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(entidad)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(entidad)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(referencia)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(referencia)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%HTMLAREA(descripcion)%%>', '<textarea name="descripcion" id="descripcion" rows="5"></textarea>', $templateCode);
		$templateCode = str_replace('<%%HTMLAREA(conslusion)%%>', '<textarea name="conslusion" id="conslusion" rows="5"></textarea>', $templateCode);
		$templateCode = str_replace('<%%VALUE(creado)%%>', '<%%creationDateTime%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(creado)%%>', urlencode('<%%creationDateTime%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(creado_por)%%>', '<%%creatorUsername%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(creado_por)%%>', urlencode('<%%creatorUsername%%>'), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('genera_documentos');
	if($selected_id) {
		$jdata = get_joined_record('genera_documentos', $selected_id);
		if($jdata === false) $jdata = get_defaults('genera_documentos');
		$rdata = $row;
	}
	$templateCode .= loadView('genera_documentos-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: genera_documentos_dv
	if(function_exists('genera_documentos_dv')) {
		$args=[];
		genera_documentos_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}