function hojadevida_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('hojadevida');
	if(!$arrPerm['insert']) return false;

	$data = [
		'dispo_codigo' => Request::lookup('dispo_codigo', ''),
		'dispo_serial' => Request::lookup('dispo_codigo'),
		'hardware' => Request::val('hardware', ''),
		'software' => Request::val('software', ''),
		'carga' => Request::fileUpload('carga', [
			'maxSize' => 5120000,
			'types' => 'doc|docx|pdf|jpg|jpge|bpm|png',
			'noRename' => true,
			'dir' => '',
			'success' => function($name, $selected_id) {
			},
			'failure' => function($selected_id, $fileRemoved) {
				if(!isset($_REQUEST['SelectedID'])) return '';

				/* for empty upload fields, when saving a copy of an existing record, copy the original upload field */
				return existing_value('hojadevida', 'carga', $_REQUEST['SelectedID']);
			},
		]),
		'creado' => parseCode('<%%creationDateTime%%>', true, true),
		'creado_por' => parseCode('<%%creatorUsername%%>', true),
	];

	if($data['dispo_codigo'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Codigo': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}

	// hook: hojadevida_before_insert
	if(function_exists('hojadevida_before_insert')) {
		$args = [];
		if(!hojadevida_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('hojadevida', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	update_calc_fields('hojadevida', $recID, calculated_fields()['hojadevida']);

	// hook: hojadevida_after_insert
	if(function_exists('hojadevida_after_insert')) {
		$res = sql("SELECT * FROM `hojadevida` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=[];
		if(!hojadevida_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('hojadevida', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) hojadevida_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

