	$templateCode = str_replace('<%%UPLOADFILE(dispo_codigo)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(hardware)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(software)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(carga)%%>', ($noUploads ? '' : "<div>{$Translation['upload image']}</div>" . '<i class="glyphicon glyphicon-remove text-danger clear-upload hidden"></i> <input type="file" name="carga" id="carga" data-filetypes="doc|docx|pdf|jpg|jpge|bpm|png" data-maxsize="5120000" accept=".doc,.docx,.pdf,.jpg,.jpge,.bpm,.png">'), $templateCode);
	if($AllowUpdate && $row['carga'] != '') {
		$templateCode = str_replace('<%%REMOVEFILE(carga)%%>', '<br><input type="checkbox" name="carga_remove" id="carga_remove" value="1"> <label for="carga_remove" style="color: red; font-weight: bold;">'.$Translation['remove image'].'</label>', $templateCode);
	} else {
		$templateCode = str_replace('<%%REMOVEFILE(carga)%%>', '', $templateCode);
	}
	$templateCode = str_replace('<%%UPLOADFILE(creado)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(creado_por)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(editado)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(editado_por)%%>', '', $templateCode);

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(dispo_codigo)%%>', safe_html($urow['dispo_codigo']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(dispo_codigo)%%>', html_attr($row['dispo_codigo']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(dispo_codigo)%%>', urlencode($urow['dispo_codigo']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(hardware)%%>', safe_html($urow['hardware']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(hardware)%%>', html_attr($row['hardware']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(hardware)%%>', urlencode($urow['hardware']), $templateCode);
		if($AllowUpdate || $AllowInsert) {
			$templateCode = str_replace('<%%HTMLAREA(software)%%>', '<textarea name="software" id="software" rows="5">' . html_attr($row['software']) . '</textarea>', $templateCode);
		} else {
			$templateCode = str_replace('<%%HTMLAREA(software)%%>', '<div id="software" class="form-control-static">' . $row['software'] . '</div>', $templateCode);
		}
		$templateCode = str_replace('<%%VALUE(software)%%>', nl2br($row['software']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(software)%%>', urlencode($urow['software']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(carga)%%>', safe_html($urow['carga']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(carga)%%>', html_attr($row['carga']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(carga)%%>', urlencode($urow['carga']), $templateCode);
		$templateCode = str_replace('<%%VALUE(creado)%%>', app_datetime($row['creado'], 'dt'), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(creado)%%>', urlencode(app_datetime($urow['creado'], 'dt')), $templateCode);
		$templateCode = str_replace('<%%VALUE(creado_por)%%>', safe_html($urow['creado_por']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(creado_por)%%>', urlencode($urow['creado_por']), $templateCode);
		$templateCode = str_replace('<%%VALUE(editado)%%>', app_datetime($row['editado'], 'dt'), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(editado)%%>', urlencode(app_datetime($urow['editado'], 'dt')), $templateCode);
		$templateCode = str_replace('<%%VALUE(editado_por)%%>', safe_html($urow['editado_por']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(editado_por)%%>', urlencode($urow['editado_por']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(dispo_codigo)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(dispo_codigo)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(hardware)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(hardware)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%HTMLAREA(software)%%>', '<textarea name="software" id="software" rows="5"></textarea>', $templateCode);
		$templateCode = str_replace('<%%VALUE(carga)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(carga)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(creado)%%>', '<%%creationDateTime%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(creado)%%>', urlencode('<%%creationDateTime%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(creado_por)%%>', '<%%creatorUsername%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(creado_por)%%>', urlencode('<%%creatorUsername%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(editado)%%>', '<%%editingDateTime%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(editado)%%>', urlencode('<%%editingDateTime%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(editado_por)%%>', '<%%editorUsername%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(editado_por)%%>', urlencode('<%%editorUsername%%>'), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
			$templateCode.="\n\tif(document.getElementById('cargaEdit')) { document.getElementById('cargaEdit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('cargaEditLink')) { document.getElementById('cargaEditLink').style.display='none'; }";
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';

	$templateCode .= "\tdispo_codigo_update_autofills$rnd1 = function() {\n";
	$templateCode .= "\t\t\$j.ajax({\n";
	if($dvprint) {
		$templateCode .= "\t\t\turl: 'hojadevida_autofill.php?rnd1=$rnd1&mfk=dispo_codigo&id=' + encodeURIComponent('".addslashes($row['dispo_codigo'])."'),\n";
		$templateCode .= "\t\t\tcontentType: 'application/x-www-form-urlencoded; charset=" . datalist_db_encoding . "',\n";
		$templateCode .= "\t\t\ttype: 'GET'\n";
	} else {
		$templateCode .= "\t\t\turl: 'hojadevida_autofill.php?rnd1=$rnd1&mfk=dispo_codigo&id=' + encodeURIComponent(AppGini.current_dispo_codigo{$rnd1}.value),\n";
		$templateCode .= "\t\t\tcontentType: 'application/x-www-form-urlencoded; charset=" . datalist_db_encoding . "',\n";
		$templateCode .= "\t\t\ttype: 'GET',\n";
		$templateCode .= "\t\t\tbeforeSend: function() { \$j('#dispo_codigo$rnd1').prop('disabled', true); \$j('#dispo_codigoLoading').html('<img src=loading.gif align=top>'); },\n";
		$templateCode .= "\t\t\tcomplete: function() { " . (($arrPerm[1] || (($arrPerm[3] == 1 && $ownerMemberID == getLoggedMemberID()) || ($arrPerm[3] == 2 && $ownerGroupID == getLoggedGroupID()) || $arrPerm[3] == 3)) ? "\$j('#dispo_codigo$rnd1').prop('disabled', false); " : "\$j('#dispo_codigo$rnd1').prop('disabled', true); ")."\$j('#dispo_codigoLoading').html(''); \$j(window).resize(); }\n";
	}
	$templateCode .= "\t\t});\n";
	$templateCode .= "\t};\n";
	if(!$dvprint) $templateCode .= "\tif(\$j('#dispo_codigo_caption').length) \$j('#dispo_codigo_caption').click(function() { dispo_codigo_update_autofills$rnd1(); });\n";


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('hojadevida');
	if($selected_id) {
		$jdata = get_joined_record('hojadevida', $selected_id);
		if($jdata === false) $jdata = get_defaults('hojadevida');
		$rdata = $row;
	}
	$templateCode .= loadView('hojadevida-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: hojadevida_dv
	if(function_exists('hojadevida_dv')) {
		$args=[];
		hojadevida_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}