		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	// get existing values
	$old_data = getRecord('mantenimientos', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: mantenimientos_before_update
	if(function_exists('mantenimientos_before_update')) {
		$args = ['old_data' => $old_data];
		if(!mantenimientos_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'mantenimientos', 
		backtick_keys_once($set), 
		['`id_mtto`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="mantenimientos_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('mantenimientos', $data['selectedID'], calculated_fields()['mantenimientos']);

	// hook: mantenimientos_after_update
	if(function_exists('mantenimientos_after_update')) {
		$res = sql("SELECT * FROM `mantenimientos` WHERE `id_mtto`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id_mtto'];
		$args = ['old_data' => $old_data];
		if(!mantenimientos_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='mantenimientos' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

