				createThumbnail($name, getThumbnailSpecs('marca_modelo', 'img', 'tv'));
				createThumbnail($name, getThumbnailSpecs('marca_modelo', 'img', 'dv'));
			},
			'failure' => function($selected_id, $fileRemoved) {
				if(!isset($_REQUEST['SelectedID'])) return '';

				/* for empty upload fields, when saving a copy of an existing record, copy the original upload field */
				return existing_value('marca_modelo', 'img', $_REQUEST['SelectedID']);
			},
		]),
