function marca_modelo_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('marca_modelo', $selected_id, 'edit')) return false;

	$data = [
		'marca' => Request::lookup('marca', ''),
		'modelo' => Request::val('modelo', ''),
		'img' => Request::fileUpload('img', [
			'maxSize' => 1843200,
			'types' => 'jpg|jpeg|gif|png',
			'noRename' => false,
			'dir' => '',
			'id' => $selected_id,
			'success' => function($name, $selected_id) {
				createThumbnail($name, getThumbnailSpecs('marca_modelo', 'img', 'tv'));
				createThumbnail($name, getThumbnailSpecs('marca_modelo', 'img', 'dv'));
			},
			'failure' => function($selected_id, $fileRemoved) {
				if($fileRemoved) return '';
				return existing_value('marca_modelo', 'img', $selected_id);
			},
		]),
		'fabricante' => Request::val('fabricante', ''),
		'descripcion' => Request::val('descripcion', ''),
	];

	if($data['marca'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Marca': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['modelo'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Modelo': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['img'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Imagen': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	// get existing values
	$old_data = getRecord('marca_modelo', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: marca_modelo_before_update
	if(function_exists('marca_modelo_before_update')) {
		$args = ['old_data' => $old_data];
		if(!marca_modelo_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'marca_modelo', 
		backtick_keys_once($set), 
		['`id_mmodelo`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="marca_modelo_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('marca_modelo', $data['selectedID'], calculated_fields()['marca_modelo']);

	// hook: marca_modelo_after_update
	if(function_exists('marca_modelo_after_update')) {
		$res = sql("SELECT * FROM `marca_modelo` WHERE `id_mmodelo`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id_mmodelo'];
		$args = ['old_data' => $old_data];
		if(!marca_modelo_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='marca_modelo' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

function marca_modelo_form($selected_id = '', $AllowUpdate = 1, $AllowInsert = 1, $AllowDelete = 1, $ShowCancel = 0, $TemplateDV = '', $TemplateDVP = '') {
