function mobiliario_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('mobiliario');
	if(!$arrPerm['insert']) return false;

	$data = [
		'codigo' => Request::val('codigo', ''),
		'grupo' => Request::lookup('grupo', ''),
		'tipo_mobiliario' => Request::lookup('tipo_mobiliario', ''),
		'descripcion' => Request::val('descripcion', ''),
		'fecha_ingreso' => Request::dateComponents('fecha_ingreso', ''),
		'accesorios' => Request::val('accesorios', ''),
		'foto' => Request::fileUpload('foto', [
			'maxSize' => 1024000,
			'types' => 'jpg|jpeg|gif|png',
			'noRename' => false,
			'dir' => '',
			'success' => function($name, $selected_id) {
				createThumbnail($name, getThumbnailSpecs('mobiliario', 'foto', 'tv'));
				createThumbnail($name, getThumbnailSpecs('mobiliario', 'foto', 'dv'));
			},
			'failure' => function($selected_id, $fileRemoved) {
				if(!isset($_REQUEST['SelectedID'])) return '';

				/* for empty upload fields, when saving a copy of an existing record, copy the original upload field */
				return existing_value('mobiliario', 'foto', $_REQUEST['SelectedID']);
			},
		]),
		'unidad' => Request::lookup('unidad', ''),
		'uni_abreviado' => Request::lookup('unidad'),
		'creado' => parseCode('<%%creationDateTime%%>', true, true),
		'creado_por' => parseCode('<%%creatorUsername%%>', true),
		'id_compra' => Request::val('id_compra', ''),
	];

	if($data['tipo_mobiliario'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Tipo de mobiliario': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['fecha_ingreso'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Fecha de ingreso': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}

	// hook: mobiliario_before_insert
	if(function_exists('mobiliario_before_insert')) {
		$args = [];
		if(!mobiliario_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('mobiliario', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	update_calc_fields('mobiliario', $recID, calculated_fields()['mobiliario']);

	// hook: mobiliario_after_insert
	if(function_exists('mobiliario_after_insert')) {
		$res = sql("SELECT * FROM `mobiliario` WHERE `id_mobiliario`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=[];
		if(!mobiliario_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('mobiliario', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) mobiliario_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

