function mobiliario_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('mobiliario', $selected_id, 'edit')) return false;

	$data = [
		'codigo' => Request::val('codigo', ''),
		'grupo' => Request::lookup('grupo', ''),
		'tipo_mobiliario' => Request::lookup('tipo_mobiliario', ''),
		'descripcion' => Request::val('descripcion', ''),
		'fecha_ingreso' => Request::dateComponents('fecha_ingreso', ''),
		'accesorios' => Request::val('accesorios', ''),
		'foto' => Request::fileUpload('foto', [
			'maxSize' => 1024000,
			'types' => 'jpg|jpeg|gif|png',
			'noRename' => false,
			'dir' => '',
			'id' => $selected_id,
			'success' => function($name, $selected_id) {
				createThumbnail($name, getThumbnailSpecs('mobiliario', 'foto', 'tv'));
				createThumbnail($name, getThumbnailSpecs('mobiliario', 'foto', 'dv'));
			},
			'failure' => function($selected_id, $fileRemoved) {
				if($fileRemoved) return '';
				return existing_value('mobiliario', 'foto', $selected_id);
			},
		]),
		'unidad' => Request::lookup('unidad', ''),
		'uni_abreviado' => Request::lookup('unidad'),
		'editado' => parseCode('<%%editingDateTime%%>', false),
		'editado_por' => parseCode('<%%editorUsername%%>', false),
		'id_compra' => Request::val('id_compra', ''),
	];

	if($data['tipo_mobiliario'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Tipo de mobiliario': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['fecha_ingreso'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Fecha de ingreso': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	// get existing values
	$old_data = getRecord('mobiliario', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: mobiliario_before_update
	if(function_exists('mobiliario_before_update')) {
		$args = ['old_data' => $old_data];
		if(!mobiliario_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'mobiliario', 
		backtick_keys_once($set), 
		['`id_mobiliario`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="mobiliario_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('mobiliario', $data['selectedID'], calculated_fields()['mobiliario']);

	// hook: mobiliario_after_update
	if(function_exists('mobiliario_after_update')) {
		$res = sql("SELECT * FROM `mobiliario` WHERE `id_mobiliario`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id_mobiliario'];
		$args = ['old_data' => $old_data];
		if(!mobiliario_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='mobiliario' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

