	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='observaciones' AND `pkValue`='{$selected_id}'", $eo);
}

function observaciones_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('observaciones', $selected_id, 'edit')) return false;

	$data = [
		'estado_observacion' => Request::lookup('estado_observacion', ''),
		'estado' => Request::lookup('estado', ''),
		'codigo' => Request::lookup('codigo', ''),
		'tipo_dispo' => Request::lookup('codigo'),
		'serial' => Request::lookup('codigo'),
		'unidad' => Request::lookup('unidad', ''),
		'observacion' => Request::val('observacion', ''),
	];

	if($data['estado'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Estado del dispositivo': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['unidad'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Unidad': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['observacion'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Observacion': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	// get existing values
	$old_data = getRecord('observaciones', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: observaciones_before_update
	if(function_exists('observaciones_before_update')) {
		$args = ['old_data' => $old_data];
		if(!observaciones_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'observaciones', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="observaciones_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('observaciones', $data['selectedID'], calculated_fields()['observaciones']);

	// hook: observaciones_after_update
	if(function_exists('observaciones_after_update')) {
		$res = sql("SELECT * FROM `observaciones` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!observaciones_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='observaciones' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

