function perfil_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('perfil', $selected_id, 'edit')) return false;

	$data = [
		'nombre' => Request::val('nombre', ''),
		'apellidos' => Request::val('apellidos', ''),
		'tipo_id' => Request::lookup('tipo_id', ''),
		'numero_id' => Request::val('numero_id', ''),
		'fecha_nacimiento' => Request::dateComponents('fecha_nacimiento', ''),
		'telefono' => Request::val('telefono', ''),
		'celular' => Request::val('celular', ''),
		'rh' => Request::lookup('rh', ''),
		'foto_perfil' => Request::fileUpload('foto_perfil', [
			'maxSize' => 1843200,
			'types' => 'jpg|jpeg|gif|png',
			'noRename' => false,
			'dir' => '',
			'id' => $selected_id,
			'success' => function($name, $selected_id) {
				createThumbnail($name, getThumbnailSpecs('perfil', 'foto_perfil', 'tv'));
				createThumbnail($name, getThumbnailSpecs('perfil', 'foto_perfil', 'dv'));
			},
			'failure' => function($selected_id, $fileRemoved) {
				if($fileRemoved) return '';
				return existing_value('perfil', 'foto_perfil', $selected_id);
			},
		]),
		'estado_civil' => Request::val('estado_civil', ''),
		'direccion' => br2nl(Request::val('direccion', '')),
		'optimo' => Request::val('optimo', ''),
		'estado' => Request::val('estado', ''),
	];

	if($data['nombre'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Nombre': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['apellidos'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Apellidos': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['tipo_id'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Tipo id': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['numero_id'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Numero id': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['fecha_nacimiento'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Fecha nacimiento': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['telefono'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Telefono': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['celular'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Celular': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	// get existing values
	$old_data = getRecord('perfil', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: perfil_before_update
	if(function_exists('perfil_before_update')) {
		$args = ['old_data' => $old_data];
		if(!perfil_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'perfil', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="perfil_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('perfil', $data['selectedID'], calculated_fields()['perfil']);

	// hook: perfil_after_update
	if(function_exists('perfil_after_update')) {
		$res = sql("SELECT * FROM `perfil` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!perfil_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='perfil' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

function perfil_form($selected_id = '', $AllowUpdate = 1, $AllowInsert = 1, $AllowDelete = 1, $ShowCancel = 0, $TemplateDV = '', $TemplateDVP = '') {
	// function to return an editable form for a table records
	// and fill it with data of record whose ID is $selected_id. If $selected_id
	// is empty, an empty form is shown, with only an 'Add New'
	// button displayed.

	global $Translation;

	// mm: get table permissions
	$arrPerm = getTablePermissions('perfil');
	if(!$arrPerm['insert'] && $selected_id=='') return $Translation['tableAccessDenied'];
	$AllowInsert = ($arrPerm['insert'] ? true : false);
	// print preview?
	$dvprint = false;
	if($selected_id && $_REQUEST['dvprint_x'] != '') {
		$dvprint = true;
	}

	$filterer_tipo_id = thisOr($_REQUEST['filterer_tipo_id'], '');
	$filterer_rh = thisOr($_REQUEST['filterer_rh'], '');

	// populate filterers, starting from children to grand-parents

	// unique random identifier
	$rnd1 = ($dvprint ? rand(1000000, 9999999) : '');
	// combobox: tipo_id
	$combo_tipo_id = new DataCombo;
	// combobox: fecha_nacimiento
	$combo_fecha_nacimiento = new DateCombo;
	$combo_fecha_nacimiento->DateFormat = "dmy";
	$combo_fecha_nacimiento->MinYear = 1900;
	$combo_fecha_nacimiento->MaxYear = 2100;
	$combo_fecha_nacimiento->DefaultDate = parseMySQLDate('', '');
	$combo_fecha_nacimiento->MonthNames = $Translation['month names'];
	$combo_fecha_nacimiento->NamePrefix = 'fecha_nacimiento';
	// combobox: rh
	$combo_rh = new DataCombo;
	// combobox: estado_civil
	$combo_estado_civil = new Combo;
	$combo_estado_civil->ListType = 0;
	$combo_estado_civil->MultipleSeparator = ', ';
	$combo_estado_civil->ListBoxHeight = 10;
	$combo_estado_civil->RadiosPerLine = 1;
	if(is_file(dirname(__FILE__).'/hooks/perfil.estado_civil.csv')) {
		$estado_civil_data = addslashes(implode('', @file(dirname(__FILE__).'/hooks/perfil.estado_civil.csv')));
		$combo_estado_civil->ListItem = explode('||', entitiesToUTF8(convertLegacyOptions($estado_civil_data)));
		$combo_estado_civil->ListData = $combo_estado_civil->ListItem;
	} else {
		$combo_estado_civil->ListItem = explode('||', entitiesToUTF8(convertLegacyOptions("SOLTERO;;CASADO;;UNION LIBRE")));
		$combo_estado_civil->ListData = $combo_estado_civil->ListItem;
	}
	$combo_estado_civil->SelectName = 'estado_civil';

	if($selected_id) {
		// mm: check member permissions
		if(!$arrPerm['view']) return $Translation['tableAccessDenied'];

		// mm: who is the owner?
		$ownerGroupID = sqlValue("SELECT `groupID` FROM `membership_userrecords` WHERE `tableName`='perfil' AND `pkValue`='" . makeSafe($selected_id) . "'");
		$ownerMemberID = sqlValue("SELECT LCASE(`memberID`) FROM `membership_userrecords` WHERE `tableName`='perfil' AND `pkValue`='" . makeSafe($selected_id) . "'");

		if($arrPerm['view'] == 1 && getLoggedMemberID() != $ownerMemberID) return $Translation['tableAccessDenied'];
		if($arrPerm['view'] == 2 && getLoggedGroupID() != $ownerGroupID) return $Translation['tableAccessDenied'];

		// can edit?
		$AllowUpdate = 0;
		if(($arrPerm['edit'] == 1 && $ownerMemberID == getLoggedMemberID()) || ($arrPerm['edit'] == 2 && $ownerGroupID == getLoggedGroupID()) || $arrPerm['edit'] == 3) {
			$AllowUpdate = 1;
		}

		$res = sql("SELECT * FROM `perfil` WHERE `id`='" . makeSafe($selected_id) . "'", $eo);
		if(!($row = db_fetch_array($res))) {
			return error_message($Translation['No records found'], 'perfil_view.php', false);
		}
		$combo_tipo_id->SelectedData = $row['tipo_id'];
		$combo_fecha_nacimiento->DefaultDate = $row['fecha_nacimiento'];
		$combo_rh->SelectedData = $row['rh'];
		$combo_estado_civil->SelectedData = $row['estado_civil'];
		$urow = $row; /* unsanitized data */
		$row = array_map('safe_html', $row);
	} else {
		$combo_tipo_id->SelectedData = $filterer_tipo_id;
		$combo_rh->SelectedData = $filterer_rh;
		$combo_estado_civil->SelectedText = ( $_REQUEST['FilterField'][1] == '13' && $_REQUEST['FilterOperator'][1] == '<=>' ? $_REQUEST['FilterValue'][1] : '');
	}
	$combo_tipo_id->HTML = '<span id="tipo_id-container' . $rnd1 . '"></span><input type="hidden" name="tipo_id" id="tipo_id' . $rnd1 . '" value="' . html_attr($combo_tipo_id->SelectedData) . '">';
	$combo_tipo_id->MatchText = '<span id="tipo_id-container-readonly' . $rnd1 . '"></span><input type="hidden" name="tipo_id" id="tipo_id' . $rnd1 . '" value="' . html_attr($combo_tipo_id->SelectedData) . '">';
	$combo_rh->HTML = '<span id="rh-container' . $rnd1 . '"></span><input type="hidden" name="rh" id="rh' . $rnd1 . '" value="' . html_attr($combo_rh->SelectedData) . '">';
	$combo_rh->MatchText = '<span id="rh-container-readonly' . $rnd1 . '"></span><input type="hidden" name="rh" id="rh' . $rnd1 . '" value="' . html_attr($combo_rh->SelectedData) . '">';
	$combo_estado_civil->Render();

