function perfil_soportes_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('perfil_soportes');
	if(!$arrPerm['insert']) return false;

	$data = [
		'tipo_documento' => Request::lookup('tipo_documento', ''),
		'nombre_documento' => Request::val('nombre_documento', ''),
		'docuemento' => Request::fileUpload('docuemento', [
			'maxSize' => 2048000,
			'types' => 'pdf|jpg|png|doc|docx',
			'noRename' => false,
			'dir' => '',
			'success' => function($name, $selected_id) {
			},
			'failure' => function($selected_id, $fileRemoved) {
				if(!isset($_REQUEST['SelectedID'])) return '';

				/* for empty upload fields, when saving a copy of an existing record, copy the original upload field */
				return existing_value('perfil_soportes', 'docuemento', $_REQUEST['SelectedID']);
			},
		]),
		'creado' => parseCode('<%%creationDateTime%%>', true, true),
		'creado_por' => parseCode('<%%creatorUsername%%>', true),
	];


	// hook: perfil_soportes_before_insert
	if(function_exists('perfil_soportes_before_insert')) {
		$args = [];
		if(!perfil_soportes_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('perfil_soportes', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	// automatic id_perfil if passed as filterer
	if($_REQUEST['filterer_id_perfil']) {
		sql("UPDATE `perfil_soportes` SET `id_perfil`='" . makeSafe($_REQUEST['filterer_id_perfil']) . "' WHERE `id`='" . makeSafe($recID, false) . "'", $eo);
	}

	update_calc_fields('perfil_soportes', $recID, calculated_fields()['perfil_soportes']);

	// hook: perfil_soportes_after_insert
	if(function_exists('perfil_soportes_after_insert')) {
		$res = sql("SELECT * FROM `perfil_soportes` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=[];
		if(!perfil_soportes_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('perfil_soportes', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) perfil_soportes_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

