}

function perfil_soportes_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('perfil_soportes', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: perfil_soportes_before_delete
	if(function_exists('perfil_soportes_before_delete')) {
		$args = [];
		if(!perfil_soportes_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: '' 
			);
	}

	sql("DELETE FROM `perfil_soportes` WHERE `id`='{$selected_id}'", $eo);

	// hook: perfil_soportes_after_delete
	if(function_exists('perfil_soportes_after_delete')) {
		$args = [];
		perfil_soportes_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='perfil_soportes' AND `pkValue`='{$selected_id}'", $eo);
}

function perfil_soportes_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('perfil_soportes', $selected_id, 'edit')) return false;

	$data = [
		'tipo_documento' => Request::lookup('tipo_documento', ''),
		'nombre_documento' => Request::val('nombre_documento', ''),
		'docuemento' => Request::fileUpload('docuemento', [
			'maxSize' => 2048000,
			'types' => 'pdf|jpg|png|doc|docx',
			'noRename' => false,
			'dir' => '',
			'id' => $selected_id,
			'success' => function($name, $selected_id) {
			},
			'failure' => function($selected_id, $fileRemoved) {
				if($fileRemoved) return '';
				return existing_value('perfil_soportes', 'docuemento', $selected_id);
			},
		]),
	];

	// get existing values
	$old_data = getRecord('perfil_soportes', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: perfil_soportes_before_update
	if(function_exists('perfil_soportes_before_update')) {
		$args = ['old_data' => $old_data];
		if(!perfil_soportes_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'perfil_soportes', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="perfil_soportes_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('perfil_soportes', $data['selectedID'], calculated_fields()['perfil_soportes']);

	// hook: perfil_soportes_after_update
	if(function_exists('perfil_soportes_after_update')) {
		$res = sql("SELECT * FROM `perfil_soportes` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!perfil_soportes_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='perfil_soportes' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

