			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		}
		if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3) { // allow delete?
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '<button type="submit" class="btn btn-danger" id="delete" name="delete_x" value="1" onclick="return confirm(\'' . $Translation['are you sure?'] . '\');" title="' . html_attr($Translation['Delete']) . '"><i class="glyphicon glyphicon-trash"></i> ' . $Translation['Delete'] . '</button>', $templateCode);
		} else {
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		}
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', ($ShowCancel ? '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>' : ''), $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(($selected_id && !$AllowUpdate && !$AllowInsert) || (!$selected_id && !$AllowInsert)) {
		$jsReadOnly = '';
		$jsReadOnly .= "\tjQuery('#tipo_documento').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#tipo_documento_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#nombre_documento').replaceWith('<div class=\"form-control-static\" id=\"nombre_documento\">' + (jQuery('#nombre_documento').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#docuemento').replaceWith('<div class=\"form-control-static\" id=\"docuemento\">' + (jQuery('#docuemento').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#docuemento, #docuemento-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	} elseif($AllowInsert) {
		$jsEditable = "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(id_perfil)%%>', $combo_id_perfil->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(id_perfil)%%>', $combo_id_perfil->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(id_perfil)%%>', urlencode($combo_id_perfil->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(tipo_documento)%%>', $combo_tipo_documento->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(tipo_documento)%%>', $combo_tipo_documento->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(tipo_documento)%%>', urlencode($combo_tipo_documento->MatchText), $templateCode);

	/* lookup fields array: 'lookup field name' => array('parent table name', 'lookup field caption') */
	$lookup_fields = array('id_perfil' => array('perfil', 'Usuario'), 'tipo_documento' => array('tipo_documento', 'Tipo documento'), );
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent hspacer-md" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] /* && !$_REQUEST['Embedded']*/) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(id_perfil)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(tipo_documento)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(nombre_documento)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(docuemento)%%>', ($noUploads ? '' : "<div>{$Translation['upload image']}</div>" . '<i class="glyphicon glyphicon-remove text-danger clear-upload hidden"></i> <input type="file" name="docuemento" id="docuemento" data-filetypes="pdf|jpg|png|doc|docx" data-maxsize="2048000" accept=".pdf,.jpg,.png,.doc,.docx">'), $templateCode);
	if($AllowUpdate && $row['docuemento'] != '') {
		$templateCode = str_replace('<%%REMOVEFILE(docuemento)%%>', '<br><input type="checkbox" name="docuemento_remove" id="docuemento_remove" value="1"> <label for="docuemento_remove" style="color: red; font-weight: bold;">'.$Translation['remove image'].'</label>', $templateCode);
	} else {
		$templateCode = str_replace('<%%REMOVEFILE(docuemento)%%>', '', $templateCode);
	}
	$templateCode = str_replace('<%%UPLOADFILE(creado)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(creado_por)%%>', '', $templateCode);

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		$templateCode = str_replace('<%%VALUE(id_perfil)%%>', safe_html($urow['id_perfil']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id_perfil)%%>', urlencode($urow['id_perfil']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(tipo_documento)%%>', safe_html($urow['tipo_documento']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(tipo_documento)%%>', html_attr($row['tipo_documento']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(tipo_documento)%%>', urlencode($urow['tipo_documento']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(nombre_documento)%%>', safe_html($urow['nombre_documento']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(nombre_documento)%%>', html_attr($row['nombre_documento']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(nombre_documento)%%>', urlencode($urow['nombre_documento']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(docuemento)%%>', safe_html($urow['docuemento']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(docuemento)%%>', html_attr($row['docuemento']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(docuemento)%%>', urlencode($urow['docuemento']), $templateCode);
		$templateCode = str_replace('<%%VALUE(creado)%%>', app_datetime($row['creado'], 'dt'), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(creado)%%>', urlencode(app_datetime($urow['creado'], 'dt')), $templateCode);
		$templateCode = str_replace('<%%VALUE(creado_por)%%>', safe_html($urow['creado_por']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(creado_por)%%>', urlencode($urow['creado_por']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(id_perfil)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id_perfil)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(tipo_documento)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(tipo_documento)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(nombre_documento)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(nombre_documento)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(docuemento)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(docuemento)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(creado)%%>', '<%%creationDateTime%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(creado)%%>', urlencode('<%%creationDateTime%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(creado_por)%%>', '<%%creatorUsername%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(creado_por)%%>', urlencode('<%%creatorUsername%%>'), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
			$templateCode.="\n\tif(document.getElementById('docuementoEdit')) { document.getElementById('docuementoEdit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('docuementoEditLink')) { document.getElementById('docuementoEditLink').style.display='none'; }";
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	if( $_REQUEST['FilterField'][1]=='2' && $_REQUEST['FilterOperator'][1]=='<=>') {
		$templateCode.="\n<input type=hidden name=id_perfil value=\"" . html_attr($_REQUEST['FilterValue'][1]) . "\">\n";
	}

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('perfil_soportes');
	if($selected_id) {
		$jdata = get_joined_record('perfil_soportes', $selected_id);
		if($jdata === false) $jdata = get_defaults('perfil_soportes');
		$rdata = $row;
	}
	$templateCode .= loadView('perfil_soportes-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: perfil_soportes_dv
	if(function_exists('perfil_soportes_dv')) {
		$args=[];
		perfil_soportes_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}