	/* lookup fields array: 'lookup field name' => array('parent table name', 'lookup field caption') */
	$lookup_fields = array('unidad' => array('unidades', 'Unidad'), );
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent hspacer-md" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] /* && !$_REQUEST['Embedded']*/) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(unidad)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(ano)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(descripcion)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(enlace)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(archivo)%%>', ($noUploads ? '' : "<div>{$Translation['upload image']}</div>" . '<i class="glyphicon glyphicon-remove text-danger clear-upload hidden"></i> <input type="file" name="archivo" id="archivo" data-filetypes="txt|doc|docx|docm|odt|pdf|rtf" data-maxsize="3072000" accept=".txt,.doc,.docx,.docm,.odt,.pdf,.rtf">'), $templateCode);
	if($AllowUpdate && $row['archivo'] != '') {
		$templateCode = str_replace('<%%REMOVEFILE(archivo)%%>', '<br><input type="checkbox" name="archivo_remove" id="archivo_remove" value="1"> <label for="archivo_remove" style="color: red; font-weight: bold;">'.$Translation['remove image'].'</label>', $templateCode);
	} else {
		$templateCode = str_replace('<%%REMOVEFILE(archivo)%%>', '', $templateCode);
	}
	$templateCode = str_replace('<%%UPLOADFILE(creado)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(creado_por)%%>', '', $templateCode);

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(unidad)%%>', safe_html($urow['unidad']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(unidad)%%>', html_attr($row['unidad']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(unidad)%%>', urlencode($urow['unidad']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(ano)%%>', safe_html($urow['ano']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(ano)%%>', html_attr($row['ano']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ano)%%>', urlencode($urow['ano']), $templateCode);
		if($dvprint || (!$AllowUpdate && !$AllowInsert)) {
			$templateCode = str_replace('<%%VALUE(descripcion)%%>', safe_html($urow['descripcion']), $templateCode);
		} else {
			$templateCode = str_replace('<%%VALUE(descripcion)%%>', safe_html($urow['descripcion'], true), $templateCode);
		}
		$templateCode = str_replace('<%%URLVALUE(descripcion)%%>', urlencode($urow['descripcion']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(enlace)%%>', safe_html($urow['enlace']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(enlace)%%>', html_attr($row['enlace']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(enlace)%%>', urlencode($urow['enlace']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(archivo)%%>', safe_html($urow['archivo']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(archivo)%%>', html_attr($row['archivo']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(archivo)%%>', urlencode($urow['archivo']), $templateCode);
		$templateCode = str_replace('<%%VALUE(creado)%%>', app_datetime($row['creado'], 'dt'), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(creado)%%>', urlencode(app_datetime($urow['creado'], 'dt')), $templateCode);
		$templateCode = str_replace('<%%VALUE(creado_por)%%>', safe_html($urow['creado_por']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(creado_por)%%>', urlencode($urow['creado_por']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(unidad)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(unidad)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(ano)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ano)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(descripcion)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(descripcion)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(enlace)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(enlace)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(archivo)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(archivo)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(creado)%%>', '<%%creationDateTime%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(creado)%%>', urlencode('<%%creationDateTime%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(creado_por)%%>', '<%%creatorUsername%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(creado_por)%%>', urlencode('<%%creatorUsername%%>'), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
			$templateCode.="\n\tif(document.getElementById('enlaceEdit')) { document.getElementById('enlaceEdit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('enlaceEditLink')) { document.getElementById('enlaceEditLink').style.display='none'; }";
			$templateCode.="\n\tif(document.getElementById('archivoEdit')) { document.getElementById('archivoEdit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('archivoEditLink')) { document.getElementById('archivoEditLink').style.display='none'; }";
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('programa_mtto');
	if($selected_id) {
		$jdata = get_joined_record('programa_mtto', $selected_id);
		if($jdata === false) $jdata = get_defaults('programa_mtto');
		$rdata = $row;
	}
	$templateCode .= loadView('programa_mtto-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: programa_mtto_dv
	if(function_exists('programa_mtto_dv')) {
		$args=[];
		programa_mtto_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}