function unidades_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('unidades', $selected_id, 'edit')) return false;

	$data = [
		'nombre' => Request::val('nombre', ''),
		'abreviado' => Request::val('abreviado', ''),
		'ciudad' => Request::lookup('ciudad', ''),
		'telefono' => Request::val('telefono', ''),
		'direccion' => Request::val('direccion', ''),
		'ubicacion' => Request::val('ubicacion', ''),
		'prestador' => Request::lookup('prestador', ''),
		'logo' => Request::fileUpload('logo', [
			'maxSize' => 819200,
			'types' => 'jpg|jpeg|gif|png',
			'noRename' => false,
			'dir' => '',
			'id' => $selected_id,
			'success' => function($name, $selected_id) {
				createThumbnail($name, getThumbnailSpecs('unidades', 'logo', 'tv'));
				createThumbnail($name, getThumbnailSpecs('unidades', 'logo', 'dv'));
			},
			'failure' => function($selected_id, $fileRemoved) {
				if($fileRemoved) return '';
				return existing_value('unidades', 'logo', $selected_id);
			},
		]),
	];

	if($data['nombre'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Nombre de la unidad': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['abreviado'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Abreviado': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['telefono'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Telefono': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['direccion'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Direccion': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	// get existing values
	$old_data = getRecord('unidades', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: unidades_before_update
	if(function_exists('unidades_before_update')) {
		$args = ['old_data' => $old_data];
		if(!unidades_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'unidades', 
		backtick_keys_once($set), 
		['`id_unidades`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="unidades_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('unidades', $data['selectedID'], calculated_fields()['unidades']);

	// hook: unidades_after_update
	if(function_exists('unidades_after_update')) {
		$res = sql("SELECT * FROM `unidades` WHERE `id_unidades`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id_unidades'];
		$args = ['old_data' => $old_data];
		if(!unidades_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='unidades' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

