			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('verificacion_articulos', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	// automatic codigo if passed as filterer
	if($_REQUEST['filterer_codigo']) {
		sql("UPDATE `verificacion_articulos` SET `codigo`='" . makeSafe($_REQUEST['filterer_codigo']) . "' WHERE `id`='" . makeSafe($recID, false) . "'", $eo);
	}

	update_calc_fields('verificacion_articulos', $recID, calculated_fields()['verificacion_articulos']);

	// hook: verificacion_articulos_after_insert
	if(function_exists('verificacion_articulos_after_insert')) {
