		$args=[];
		if(!verificacion_articulos_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('verificacion_articulos', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) verificacion_articulos_copy_children($recID, $_REQUEST['SelectedID']);

