function verificacion_articulos_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('verificacion_articulos', $selected_id, 'edit')) return false;

	$data = [
		'tipo_dispo' => Request::lookup('codigo'),
		'nombre' => Request::lookup('codigo'),
		'serial' => Request::lookup('codigo'),
		'ubicacion' => Request::lookup('ubicacion', ''),
		'abreviado' => Request::lookup('ubicacion'),
		'verificacion' => Request::checkBox('verificacion', ''),
		'comentario' => br2nl(Request::val('comentario', '')),
	];

	if($data['ubicacion'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Ubicacion': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['verificacion'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Verificado': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	// get existing values
	$old_data = getRecord('verificacion_articulos', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: verificacion_articulos_before_update
	if(function_exists('verificacion_articulos_before_update')) {
		$args = ['old_data' => $old_data];
		if(!verificacion_articulos_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'verificacion_articulos', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="verificacion_articulos_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('verificacion_articulos', $data['selectedID'], calculated_fields()['verificacion_articulos']);

	// hook: verificacion_articulos_after_update
	if(function_exists('verificacion_articulos_after_update')) {
		$res = sql("SELECT * FROM `verificacion_articulos` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!verificacion_articulos_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='verificacion_articulos' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

